import React, { useContext } from "react";
import { Link, useLocation } from "react-router-dom";
import { CartContext } from "../context/CartContext"; // Adjust the import path as necessary

const Header = ({ onSearch }) => {
  const location = useLocation();
  const { cartItems } = useContext(CartContext); // Access cart items from context

  // Calculate the total number of items in the cart
  const totalItemsInCart = cartItems.reduce(
    (total, item) => total + item.quantity,
    0
  );

  return (
    <header className="bg-dark bg-gradient text-white p-3">
      <div className="container d-flex justify-content-between align-items-center">
        <h1>E-Commerce</h1>
        <nav>
          {location.pathname === "/" ? (
            <Link to="/cart" className="btn btn-light position-relative">
              Cart
              {totalItemsInCart > 0 && (
                <span className="position-absolute top-0 start-100 translate-middle badge rounded-pill bg-danger">
                  {totalItemsInCart}
                  <span className="visually-hidden">items in cart</span>
                </span>
              )}
            </Link>
          ) : (
            <Link to="/" className="btn btn-light">
              HomePage
            </Link>
          )}
        </nav>
      </div>
    </header>
  );
};

export default Header;
