import React from "react";
import { useCart } from "../../context/CartContext"; // Ensure the correct path

const CartItem = ({ item }) => {
  const { updateCartItem, removeFromCart } = useCart();

  // Increment item quantity
  const incrementQuantity = () => {
    updateCartItem(item.id, item.quantity + 1);
  };

  // Decrement item quantity
  const decrementQuantity = () => {
    if (item.quantity > 1) {
      updateCartItem(item.id, item.quantity - 1);
    } else {
      removeFromCart(item.id); // Optionally remove the item if quantity becomes less than 1
    }
  };

  // Calculate the total price for the item
  const totalPrice = (item.price * item.quantity).toFixed(2); // Rounds to 2 decimal places

  return (
    <div className="d-flex justify-content-between align-items-center border-bottom mb-3 pb-3">
      <div>
        <h5>{item.title}</h5>
        <p>Price: $ {item.price}</p>
        <p>Quantity: {item.quantity}</p>
        <p>Total: $ {totalPrice}</p> {/* Display the total price */}
      </div>
      <div className="d-flex align-items-center">
        <button className="btn btn-secondary" onClick={decrementQuantity}>
          -
        </button>
        <div className="mx-2" style={{ minWidth: "50px", textAlign: "center" }}>
          {item.quantity}
        </div>
        <button className="btn btn-secondary" onClick={incrementQuantity}>
          +
        </button>
        <button
          className="btn btn-danger ml-2"
          onClick={() => removeFromCart(item.id)}
        >
          <i className="fas fa-trash"></i>{" "}
          {/* Use a trash can icon for the remove button */}
        </button>
      </div>
    </div>
  );
};

export default CartItem;
