import React, { useEffect, useState } from "react";
import { getProducts } from "../../api/ServerAPI";
import Product from "./Product";
import SearchBar from "../Search/SearchBar";
import { useCart } from "../../context/CartContext";

const ProductList = () => {
  const [products, setProducts] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [searchType, setSearchType] = useState("name"); // Add searchType state
  const { addToCart } = useCart();

  useEffect(() => {
    getProducts()
      .then((data) => {
        setProducts(data.products);
      })
      .catch((error) => console.error("Failed to fetch products:", error));
  }, []);

  // Filter products based on searchTerm and searchType
  const filteredProducts = products.filter((product) => {
    const term = searchTerm.toLowerCase();
    if (searchType === "name") {
      return product.title.toLowerCase().includes(term);
    } else if (searchType === "category") {
      return product.category.toLowerCase().includes(term);
    }
    return false;
  });

  return (
    <>
      <div className="search-container">
        <SearchBar
          onSetSearchCriteria={setSearchTerm}
          onSetSearchType={setSearchType}
        />
      </div>
      <div className="row">
        {filteredProducts.map((product) => (
          <Product
            key={product.id}
            product={product}
            onAddToCart={() => addToCart(product)}
          />
        ))}
      </div>
    </>
  );
};

export default ProductList;
