import React from "react";
import Slider from "react-slick";
import "./Product.css";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const Product = ({ product, onAddToCart }) => {
  const settings = {
    dots: true,
    infinite: product.images.length > 1, // Only loop if there's more than one image
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    lazyLoad: true,
  };

  // Create a new array combining the thumbnail and images
  // Ensuring the thumbnail is first and avoiding duplicates
  const allImages = [
    product.thumbnail,
    ...product.images.filter((img) => img !== product.thumbnail),
  ];

  return (
    <div className="col-md-4 mb-4">
      <div className="card h-100">
        <Slider {...settings}>
          {allImages.map((image, index) => (
            <div key={index} className="slider-image-container">
              <img
                src={image}
                className="card-img-top"
                alt={`Product image ${index + 1}`}
              />
            </div>
          ))}
        </Slider>
        <div className="card-body d-flex flex-column">
          {/* Product details */}
        </div>
      </div>
    </div>
  );
};

export default Product;
